document.addEventListener('DOMContentLoaded', function() {
    // Curseur personnalisÃ©
    const cursor = document.querySelector('.custom-cursor');
    const links = document.querySelectorAll('a, button, .service-card, .principle');

    document.addEventListener('mousemove', function(e) {
        cursor.style.left = e.clientX + 'px';
        cursor.style.top = e.clientY + 'px';
    });

    links.forEach(link => {
        link.addEventListener('mouseenter', function() {
            cursor.classList.add('expand');
        });

        link.addEventListener('mouseleave', function() {
            cursor.classList.remove('expand');
        });
    });

    // Menu mobile
    const mobileToggle = document.querySelector('.mobile-toggle');
    const navMenu = document.querySelector('.nav-menu');

    mobileToggle.addEventListener('click', function() {
        this.classList.toggle('active');
        navMenu.classList.toggle('active');
    });

    // Navigation fluide
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            e.preventDefault();

            // Fermer le menu mobile si ouvert
            mobileToggle.classList.remove('active');
            navMenu.classList.remove('active');

            const targetId = this.getAttribute('href');
            const targetElement = document.querySelector(targetId);

            if (targetElement) {
                window.scrollTo({
                    top: targetElement.offsetTop - 80,
                    behavior: 'smooth'
                });
            }
        });
    });

    // ContrÃ´les de carousel simples
    const carousel = document.querySelector('.testimonials-carousel');
    const prevBtn = document.querySelector('.control-btn.prev');
    const nextBtn = document.querySelector('.control-btn.next');
    const cardWidth = document.querySelector('.testimonial-card').offsetWidth + 32; // +margin

    let position = 0;
    const maxPosition = carousel.children.length * cardWidth - carousel.offsetWidth;

    prevBtn.addEventListener('click', function() {
        position = Math.max(position - cardWidth, 0);
        carousel.style.transform = `translateX(-${position}px)`;
        carousel.style.transition = 'transform 0.5s ease';
    });

    nextBtn.addEventListener('click', function() {
        position = Math.min(position + cardWidth, maxPosition);
        carousel.style.transform = `translateX(-${position}px)`;
        carousel.style.transition = 'transform 0.5s ease';
    });

    // Animation au dÃ©filement
    const observerOptions = {
        root: null,
        rootMargin: '0px',
        threshold: 0.1
    };

    const observer = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                entry.target.style.opacity = '1';
                entry.target.style.transform = 'translateY(0)';
                observer.unobserve(entry.target);
            }
        });
    }, observerOptions);

    const animatedElements = document.querySelectorAll('.service-card, .philosophy-img-1, .philosophy-img-2, .principle, .testimonial-card');

    animatedElements.forEach(el => {
        el.style.opacity = '0';
        el.style.transform = 'translateY(30px)';
        el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';
        observer.observe(el);
    });
});
